<style lang="scss">
    div {
        margin-top: 10em;
    }

    section {
        margin-top: 13em;

        h2 {
            text-align: center;
            max-width: 21em; 
            padding: 1em;
            margin: 0 auto;
            display: block; 
        }

        div {
            position: relative;
            width: 17em;
            height: 0.125em;
            overflow: hidden;
            background-image: linear-gradient(to right, rgba(255, 255, 255, 0), #21DBD0, white);
            margin: 0 auto;
            border-radius: 1em;
        }

        svg {
            border-radius: 100%;
        }

        #you {
            display: block;
            margin: -25em auto; 
            animation: YouAnimation 8s ease-in-out infinite alternate;
        }
        .douwe {
            position: absolute;
            left: 22%; 
            top: 25%; 
            transform: translateY(-50%); 
            animation: DouweAnimation 6s ease-in-out infinite alternate;
        }
        .mark {
            position: absolute;
            right: 10%; 
            top: 50%; 
            transform: translateY(-50%); 
            animation: MarkAnimation 9s ease-in-out infinite alternate;
        }
        .robbin {
            position: absolute;
            left: 15%; 
            bottom: 25%; 
            animation: RobbinAnimation 7s ease-in-out infinite alternate;
        }
        .rowin {
            position: absolute;
            right: 18%;
            top: 10%;
            animation: RowinAnimation 5s ease-in-out infinite alternate;
        }
        .tyco {
            position: absolute;
            left: 35%;
            bottom: 10%;
            animation: TycoAnimation 10s ease-in-out infinite alternate;
        }
        .yunus {
            position: absolute;
            right: 22%;
            bottom: 15%;
            animation: YunusAnimation 7s ease-in-out infinite alternate;
        }
    }

    @keyframes YouAnimation {
        0% {
            transform: translate(0, 0); 
        }
        25% {
            transform: translate(5em, 5em); 
        }
        50% {
            transform: translate(0, 5em); 
        }
        75% {
            transform: translate(-50px, 50px);
        }
        100% {
            transform: translate(0, 0); 
        }
    }

    @keyframes DouweAnimation {
        0% {
            transform: translate(0, 0);
        }
        25% {
            transform: translate(30px, 70px);
        }
        50% {
            transform: translate(-45px, 35px);
        }
        75% {
            transform: translate(60px, -12.5px);
        }
        100% {
            transform: translate(0, 0);
        }
    }

    @keyframes MarkAnimation {
        0% {
            transform: translateX(0);
        }
        25% {
            transform: translateX(50px);
        }
        50% {
            transform: translateX(100px);
        }
        75% {
            transform: translateX(120px);
        }
        100% {
            transform: translateX(130px);
        }
    }

    @keyframes RobbinAnimation {
        0% {
            transform: translateY(0) translateX(0);
        }
        25% {
            transform: translateY(-50px) translateX(50px);
        }
        50% {
            transform: translateY(0) translateX(100px);
        }
        75% {
            transform: translateY(50px) translateX(50px);
        }
        100% {
            transform: translateY(0) translateX(0);
        }
    }

    @keyframes RowinAnimation {
        0% {
            transform: translateY(0);
        }
        25% {
            transform: translateY(-50px);
        }
        50% {
            transform: translateY(0);
        }
        75% {
            transform: translateY(-50px);
        }
        100% {
            transform: translateY(0);
        }
    }

    @keyframes TycoAnimation {
        0% {
            transform: translate(0, 0);
        }
        25% {
            transform: translate(-50px, -50px);
        }
        50% {
            transform: translate(-100px, -100px);
        }
        75% {
            transform: translate(-150px, -150px);
        }
        100% {
            transform: translate(-200px, -200px);
        }
    }

    @keyframes YunusAnimation {
        0% {
            transform: translateY(0);
        }
        25% {
            transform: translateY(-25px);
        }
        50% {
            transform: translateY(0);
        }
        75% {
            transform: translateY(25px);
        }
        100% {
            transform: translateY(0);
        }
    }
</style>