<style lang="scss">
    section {
        h2 {
            text-align: center;
            max-width: 21em; 
            padding: 1em;
            margin: 0 auto;
            display: block; 
        }

        div {
            position: relative;
            width: 17em;
            height: 0.125em;
            overflow: hidden;
            background-image: linear-gradient(to right, rgba(255, 255, 255, 0), #21DBD0, white);
            margin: 0 auto;
            border-radius: 1em;
        }

        svg {
            animation: floatAnimation 3s ease-in-out infinite alternate; // Definieer de animatie
        }
    }

    @keyframes floatAnimation {
        0% {
        transform: translate(0, 0); // Beginpositie
    }
    25% {
        transform: translate(5em, 5em); // Schuin naar rechts onder
    }
    50% {
        transform: translate(0, 10em); // Schuin naar beneden
    }
    75% {
        transform: translate(-50px, 50px); // Schuin naar links onder
    }
    100% {
        transform: translate(0, 0); // Terug naar beginpositie
    }
}
</style>